import React, {Component} from 'react';
import Submission from '../../Ethereum/submission';
import {Link} from '../../routes';
import web3 from '../../Ethereum/web3';
import Layout from '../../Components/Layout';
import ipfs from '../../ipfs';
import { Form, Input } from 'semantic-ui-react';

class Upload extends Component{

    constructor(props){
        super(props);
        this.state = {
            loading: false,
            errormessage: '',
            firstname: '',
            lastname: '',
            scholarnumber: '',
            buffer: null,
            ipfsHash: '',
            active: false
        }
        this.captureFile = this.captureFile.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.generate = this.generate.bind(this);
    }


    generate(event) {
        event.preventDefault();
        ipfs.files.add(this.state.buffer, (err, res) => {
            if(err){
                console.log(err)
                return
            }
            this.setState({ipfsHash: res[0].hash})
            console.log('ipfsHash: ', this.state.ipfsHash);
        })
        this.setState({active: false});
    }

    captureFile(event) {
        event.preventDefault();
        console.log('Capture File...')

        const file = event.target.files[0];
        const reader = new window.FileReader();
        reader.readAsArrayBuffer(file);

        reader.onloadend = () => {
            this.setState({buffer: Buffer(reader.result)})
            console.log('buffer ',this.state.buffer);
        }
    }

    render(){
        return(
            <Layout>
                <h3>Upload your file</h3>
                <Form>
                    <Form.Field>
                        <label>First Name</label>
                        <Input 
                         value = {this.state.firstname}
                         onChange = {event => this.setState({firstname: event.target.value})}
                        />
                        <Input 
                          value = {this.state.lastname}
                          onChange = {event => this.setState({lastname: event.target.value})}  
                        />
                        <Input 
                          value = {this.state.scholarnumber}
                          onChange = {event => this.setState({scholarnumber: event.target.value})}  
                        />
                    </Form.Field>
                </Form>
            </Layout>
        )
    }
}

export default Upload;
